import React, { useState } from "react";

const Form = () => {


  const [userInput, setUserInput] = useState({
    enteredCurrentSavings: "",
    enteredYearlySavings: "",
    enteredExpectedInterest: "",
    enteredInvestmentDuration: "",
  });

  const CurrentSavingsChangeHandler = (event) => {
    const newValue = event.target.value;

    setUserInput((prevState) => {
      return {
        ...prevState,
        enteredCurrentSavings: newValue !== "" ? parseInt(newValue, 10) : "",
      };
    });
  };

  const YearlySavingsChangeHandler = (event) => {
    const newValue = event.target.value;

    setUserInput((prevState) => {
      return {
        ...prevState,
        enteredYearlySavings: newValue !== "" ? parseInt(newValue, 10) : "",
      };
    });
  };

  const ExpectedInterestChangeHandler = (event) => {
    const newValue = event.target.value;

    setUserInput((prevState) => {
      return {
        ...prevState,
        enteredExpectedInterest: newValue !== "" ? parseInt(newValue, 10) : "",
      };
    });
  };

  const InvestmentDurationChangeHandler = (event) => {
    const newValue = event.target.value;

    setUserInput((prevState) => {
      return {
        ...prevState,
        enteredInvestmentDuration:
          newValue !== "" ? parseInt(newValue, 10) : "",
      };
    });
  };

  return (
    <form className="form" onSubmit={calculateHandler}>
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            id="current-savings"
            onChange={CurrentSavingsChangeHandler}
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearly-contribution"
            onChange={YearlySavingsChangeHandler}
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            id="expected-return"
            onChange={ExpectedInterestChangeHandler}
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            id="duration"
            onChange={InvestmentDurationChangeHandler}
          />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};
export default Form;
