import FormItem from "./FormItem";

const Labels = [
  {
    htmlFor:"current-savings",
    text: "Current Savings($)",
  },
  {
    htmlFor:"yearly-contribution",
    text: "Yearly Savings($)",
  },
  {
    htmlFor:"expected-return",
    text: "Expected Interest($)",
  },
  {
    htmlFor:"duration",
    text: "Investment Duration (years)",
  },
 
];
const Form = () => {
  
  return (
    <form className="form">
      <div className="input-group">
        <FormItem  htmlfor={Labels[0].htmlFor} text={Labels[0].text}></FormItem>
        <FormItem  htmlfor={Labels[1].htmlFor} text={Labels[1].text}></FormItem>
      </div>
      <div className="input-group">
      <FormItem  htmlfor={Labels[2].htmlFor} text={Labels[2].text}></FormItem>
      <FormItem  htmlfor={Labels[3].htmlFor} text={Labels[3].text}></FormItem>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt">
          Reset
        </button>
        <button type="submit" className="button">
          Calculate
        </button>
      </p>
    </form>
  );
};
export default Form;
